import React, { createContext, useContext, useState } from 'react';
import { HOTELS } from '../data/mockData';

const BookingContext = createContext();

export function useBooking() {
  return useContext(BookingContext);
}

export function BookingProvider({ children }) {
  const [searchResults, setSearchResults] = useState(HOTELS);
  const [bookings, setBookings] = useState([]);

  function search({ destination }) {
    const q = (destination || '').toLowerCase();
    const results = HOTELS.filter(h => h.name.toLowerCase().includes(q) || h.location.toLowerCase().includes(q));
    setSearchResults(results);
  }

  function getHotelById(id) {
    return HOTELS.find(h => h.id === id);
  }

  function createBooking({ hotelId, name, email, guests, checkin, checkout }) {
    const id = `b${Date.now()}`;
    const hotel = getHotelById(hotelId);
    const total = hotel ? hotel.price * (Number(guests) || 1) : 0;
    const booking = { id, hotel, name, email, guests: Number(guests), checkin, checkout, total };
    setBookings(prev => [booking, ...prev]);
    return booking;
  }

  function getBookingById(id) {
    return bookings.find(b => b.id === id);
  }

  return (
    <BookingContext.Provider value={{ searchResults, search, createBooking, getHotelById, getBookingById, bookings }}>
      {children}
    </BookingContext.Provider>
  );
}
