import React from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { useBooking } from '../context/BookingContext';

export default function BookingDetails() {
  const { id } = useParams();
  const { getHotelById } = useBooking();
  const hotel = getHotelById(id);
  const history = useHistory();

  if (!hotel) return <main className="container"><p>Hotel not found.</p></main>;

  return (
    <main className="container">
      <h1>{hotel.name}</h1>
      <img src={hotel.img} alt={hotel.name} style={{ maxWidth: 480 }} />
      <p className="muted">{hotel.location}</p>
      <p className="muted">{hotel.description}</p>
      <p>From {new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR' }).format(hotel.price)} / night · ⭐{hotel.rating}</p>
      <button className="btn" onClick={() => history.push({ pathname: '/book', state: { hotelId: hotel.id } })}>Book this hotel</button>
    </main>
  );
}
