import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { useBooking } from '../context/BookingContext';

const BookingForm = () => {
    const { createBooking, getHotelById } = useBooking();
    const loc = useLocation();
    const hotelId = loc.state?.hotelId || '';
    const hotel = hotelId ? getHotelById(hotelId) : null;

    const [formData, setFormData] = useState({
        hotelId,
        name: '',
        email: '',
        guests: 1,
        checkin: '',
        checkout: ''
    });
    const [confirmation, setConfirmation] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const booking = createBooking(formData);
        setConfirmation(booking);
    };

        if (confirmation) {
            return (
                <div className="container">
                    <h2>Booking confirmed</h2>
                    <p>Booking ID: {confirmation.id}</p>
                    <p>Hotel: {confirmation.hotel?.name}</p>
                    <p>Total: {new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR' }).format(confirmation.total)}</p>
                </div>
            );
        }

    return (
        <form className="booking-form container" onSubmit={handleSubmit}>
            <h2>Book {hotel ? `- ${hotel.name}` : ''}</h2>
            {!hotel && (
                <input name="hotelId" placeholder="Hotel ID" value={formData.hotelId} onChange={handleChange} />
            )}
            <input name="name" placeholder="Full name" value={formData.name} onChange={handleChange} required />
            <input name="email" placeholder="Email" type="email" value={formData.email} onChange={handleChange} required />
            <input name="guests" type="number" min="1" value={formData.guests} onChange={handleChange} />
            <label>Check-in</label>
            <input name="checkin" type="date" value={formData.checkin} onChange={handleChange} />
            <label>Check-out</label>
            <input name="checkout" type="date" value={formData.checkout} onChange={handleChange} />
            <button type="submit">Confirm booking</button>
        </form>
    );
};

export default BookingForm;
