import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { useBooking } from '../context/BookingContext';

const BookingForm = () => {
    const { createBooking, getItemById } = useBooking();
    const loc = useLocation();
    const incoming = loc.state || {};

    // infer type/itemId from location state when available
    const initialItemId = incoming.itemId || incoming.hotelId || '';
    const initialType = incoming.itemType || '';

    const [item, setItem] = useState(() => (initialItemId ? getItemById(initialItemId) : null));
    const [type, setType] = useState(initialType || (item ? (item.id && item.name ? 'hotel' : '') : 'hotel'));
    const [confirmation, setConfirmation] = useState(null);

    const [formData, setFormData] = useState({
        itemId: initialItemId,
        itemType: initialType || '',
        name: '',
        email: '',
        passengers: 1,
        date: '',
        travelClass: '',
        seatPreference: '',
        extras: ''
    });

    useEffect(() => {
        if (formData.itemId) {
            const it = getItemById(formData.itemId);
            setItem(it);
            // try to set type if not provided
            if (!formData.itemType && it) {
                let detected = 'unknown';
                // simple detection by properties
                if (it.duration && it.airline) detected = 'flight';
                else if (it.operator && it.busNumber) detected = 'bus';
                else if (it.trainNumber) detected = 'train';
                else if (it.location && it.description && it.duration) detected = 'tour';
                else if (it.price && it.location && it.name) detected = 'hotel';
                setType(detected);
                setFormData(fd => ({ ...fd, itemType: detected }));
            }
        }
    }, [formData.itemId, formData.itemType, getItemById]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleTypeChange = (e) => {
        const newType = e.target.value;
        setType(newType);
        setFormData(fd => ({ ...fd, itemType: newType }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // basic validation
        if (!formData.name || !formData.email) return alert('Please provide name and email');
        const payload = {
            itemId: formData.itemId || null,
            itemType: formData.itemType || type,
            name: formData.name,
            email: formData.email,
            passengers: formData.passengers,
            date: formData.date,
            travelClass: formData.travelClass,
            seatPreference: formData.seatPreference,
            extras: { note: formData.extras }
        };

        const booking = createBooking(payload);
        setConfirmation(booking);
    };

    if (confirmation) {
        return (
            <div className="container">
                <h2>Booking confirmed</h2>
                <p>Booking ID: {confirmation.id}</p>
                <p>Item: {confirmation.item?.name || confirmation.item?.airline || confirmation.item?.operator || confirmation.item?.trainNumber || 'Selected'}</p>
                <p>Total: {new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR' }).format(confirmation.total)}</p>
            </div>
        );
    }

    return (
        <form className="booking-form container" onSubmit={handleSubmit}>
            <h2>Book {item ? `- ${item.name || item.airline || item.operator || item.trainNumber || 'Item'}` : ''}</h2>

            <label>Type</label>
            <select name="itemType" value={formData.itemType || type} onChange={handleTypeChange}>
                <option value="hotel">Hotel</option>
                <option value="flight">Flight</option>
                <option value="bus">Bus</option>
                <option value="train">Train</option>
                <option value="tour">Tour</option>
            </select>

            <label>Item ID (optional)</label>
            <input name="itemId" placeholder="Item ID (e.g. f1, b3, t2, p5, h1)" value={formData.itemId} onChange={handleChange} />

            <input name="name" placeholder="Full name" value={formData.name} onChange={handleChange} required />
            <input name="email" placeholder="Email" type="email" value={formData.email} onChange={handleChange} required />

            <label>Passengers / Guests</label>
            <input name="passengers" type="number" min="1" value={formData.passengers} onChange={handleChange} />

            {/* Type-specific fields */}
            { (formData.itemType || type) === 'hotel' && (
                <>
                    <label>Check-in</label>
                    <input name="date" type="date" value={formData.date} onChange={handleChange} />
                    <label>Special requests</label>
                    <input name="extras" placeholder="e.g. late check-in" value={formData.extras} onChange={handleChange} />
                </>
            )}

            { (formData.itemType || type) === 'flight' && (
                <>
                    <label>Departure date</label>
                    <input name="date" type="date" value={formData.date} onChange={handleChange} required />
                    <label>Class</label>
                    <select name="travelClass" value={formData.travelClass} onChange={handleChange}>
                        <option value="Economy">Economy</option>
                        <option value="Premium Economy">Premium Economy</option>
                        <option value="Business">Business</option>
                    </select>
                    <label>Seat preference</label>
                    <select name="seatPreference" value={formData.seatPreference} onChange={handleChange}>
                        <option value="Any">Any</option>
                        <option value="Window">Window</option>
                        <option value="Aisle">Aisle</option>
                    </select>
                </>
            )}

            { (formData.itemType || type) === 'bus' && (
                <>
                    <label>Travel date</label>
                    <input name="date" type="date" value={formData.date} onChange={handleChange} required />
                    <label>Seat preference</label>
                    <select name="seatPreference" value={formData.seatPreference} onChange={handleChange}>
                        <option value="Any">Any</option>
                        <option value="Front">Front</option>
                        <option value="Window">Window</option>
                    </select>
                </>
            )}

            { (formData.itemType || type) === 'train' && (
                <>
                    <label>Travel date</label>
                    <input name="date" type="date" value={formData.date} onChange={handleChange} required />
                    <label>Berth preference</label>
                    <select name="seatPreference" value={formData.seatPreference} onChange={handleChange}>
                        <option value="Any">Any</option>
                        <option value="Lower">Lower</option>
                        <option value="Upper">Upper</option>
                        <option value="Side">Side</option>
                    </select>
                </>
            )}

            { (formData.itemType || type) === 'tour' && (
                <>
                    <label>Start date</label>
                    <input name="date" type="date" value={formData.date} onChange={handleChange} required />
                    <label>Special requests</label>
                    <input name="extras" placeholder="e.g. vegetarian meals" value={formData.extras} onChange={handleChange} />
                </>
            )}

            <button type="submit">Confirm booking</button>
        </form>
    );
};

export default BookingForm;
